const Modal = ({ display, closeModal, modalItem }) => {

    let showModal = display ? "modal show-modal" : "modal";

    return (
        <div className={showModal}>
            <button onClick={() => closeModal(false)}>
                <i className="fa fa-close close-modal-icon"></i>
            </button>
            <div className="modal-info-container" >
                <div className="modal-image">
                    <img src={modalItem.image} />
                </div>
                <div className="modal-detail">
                    <h2 className="modal-title">{modalItem.title}</h2>
                    <p className="modal-desc">Description: <span>{modalItem.description}</span></p>
                    <p className="modal-category">Category: <span>{modalItem.category}</span></p>
                    <p className="modal-price">Price: <span>{modalItem.price}$</span></p>
                </div>
            </div>
        </div>
    );
}

export default Modal;
