import { useState, useEffect } from "react";
import ReactNotification, { store } from "react-notifications-component";
import "react-notifications-component/dist/theme.css";
import "animate.css";
import Header from "./Header";
import Footer from "./Footer";
import Modal from "./Modal";
import ReactLoading from "react-loading";

function Home() {
  useEffect(() => {
    fetchItems();
  }, []);

  const [items, setItems] = useState([]);
  let [cart, setCart] = useState([]);
  const [popUp, setPopUp] = useState();
  const [modalItem, setModaltem] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [search, setSearch] = useState("");
  const [ctgry, setCtgry] = useState("");

  let isModalOpen = false;

  const fetchItems = async () => {
    await fetch("https://fakestoreapi.com/products")
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setIsLoading(false);
        setItems(data);
      })
      .catch((err) => {
        console.log(err, " error");
      });
  };

  const addToCartHandler = (item, qnty = 1) => {
    const productExist = cart.find((items) => items.id === item.id);

    if (!productExist) {
      setCart([...cart, { ...item, quantity: qnty }]);
      showNotification("Success", "Product added to the cart.", "success");
    } else {
      showNotification("Warning", "Product already exist in cart.", "warning");
    }
  };

  const searchProduct = (product) => {
    const result = product.target.value;
    setSearch(result);
  };

  const getModalData = (item) => {
    setModaltem(item);
  };

  const openModal = () => {
    isModalOpen = true;
    setPopUp(isModalOpen);
  };

  const closeModal = (val) => {
    isModalOpen = val;
    setPopUp(isModalOpen);
  };

  const showNotification = (ttl, msg, typ) => {
    store.addNotification({
      title: ttl,
      message: msg,
      type: typ,
      insert: "bottom",
      container: "top-right",
      animationIn: ["animate__animated", "animate__fadeIn"],
      animationOut: ["animate__animated", "animate__fadeOut"],
      dismiss: {
        duration: 2000,
        onScreen: false,
      },
    });
  };

  const addNewProduct = (product) => {
    setItems([...items, product]);
  };

  return (
    <>
      <Header
        cart={cart}
        productsLength={items.length}
        searchProduct={searchProduct}
        addNewProduct={addNewProduct}
      />
      <ReactNotification />
      <main>
        <Modal
          modalItem={modalItem}
          closeModal={closeModal}
          display={popUp}
          addToCartHandler={addToCartHandler}
        />
        <div className="product-wrapper">
          <select className="product-category" defaultValue={"Default"}>
            <option value="Default" disabled>
              Change category
            </option>
            <option onClick={() => setCtgry("all")}>all</option>
            <option onClick={() => setCtgry("men's clothing")}>
              Mens Casualwear
            </option>
            <option onClick={() => setCtgry("women's clothing")}>
              Womens Casualwear
            </option>
            <option onClick={() => setCtgry("jewelery")}>Jewelery</option>
            <option onClick={() => setCtgry("electronics")}>Electronics</option>
          </select>
          <div className="products">
            {isLoading ? (
              <ReactLoading color={"white"} height={"10%"} width={"10%"} />
            ) : (
              items
                .filter((val) => {
                  if (search == "") {
                    return val;
                  } else if (
                    val.title.toLowerCase().includes(search.toLowerCase())
                  ) {
                    return val;
                  } else if (
                    !val.title.toLowerCase().includes(search.toLowerCase())
                  ) {
                    return <p>Item not found</p>;
                  }
                })
                .filter((val) => {
                  if (ctgry === "men's clothing") {
                    return val.category === ctgry;
                  } else if (ctgry === "women's clothing") {
                    return val.category === ctgry;
                  } else if (ctgry === "jewelery") {
                    return val.category === ctgry;
                  } else if (ctgry === "electronics") {
                    return val.category === ctgry;
                  } else if (val.category === "all") {
                    return (search = "");
                  } else {
                    return val;
                  }
                })
                .reverse()
                .map((item) => (
                  <div key={item.id} className="product-item">
                    <div className="product-image">
                      <img src={item.image} alt={item.title} />
                    </div>
                    <h3>{item.title}</h3>
                    <p>{item.price}$</p>
                    <div className="product-item-footer">
                      <button
                        onClick={() => {
                          openModal(item.id);
                          getModalData(item);
                        }}
                        className="see-product-details"
                      >
                        Quick View
                      </button>
                      <button
                        onClick={() => addToCartHandler(item)}
                        className="add-to-cart"
                      >
                        Add To Cart
                      </button>
                    </div>
                  </div>
                ))
            )}
          </div>
        </div>
      </main>
      <Footer />
    </>
  );
}

export default Home;
