import { useState } from "react";

const CartProducts = ({ name, price, image, removeFromCart, id, quantity }) => {
  // const [qnty, setQuantity] = useState(quantity);
  const amount = price * quantity;
  const fixedPrice = amount.toFixed(2);

  const incDecQuantity = (val) => {
    if (quantity > 1 && val === "minus") {
      // setQuantity(qnty - 1);
      console.log('-')
      return quantity--;
    } else {
      console.log('+')
     return quantity++;
      // setQuantity(qnty + 1);
    }
  };

  return (
    <div className="cart-prducts-wrapper">
      <ul>
        <li>
          <button onClick={() => removeFromCart(id)}>
            <i className="fa fa-remove"></i>
          </button>
        </li>
        <li>
          <div className="cart-products-img">
            <img src={image} />
          </div>
        </li>
        <li className="cart-products-name">{name}</li>
        <li className="cart-products-price">${price}</li>
        <li className="cart-products-quantity">
          <i
            onClick={() => incDecQuantity("minus")}
            className="fa fa-minus"
          ></i>
          <span>{quantity}</span>
          <i onClick={() => incDecQuantity("plus")} className="fa fa-plus"></i>
        </li>
        <li className="cart-products-price">${fixedPrice}</li>
      </ul>
    </div>
  );
};
export default CartProducts;
