import React, {useState} from 'react';
import Footer from './Assets/components/Footer';
import Header from './Assets/components/Header';
import ProductItem from './Assets/components/ProductItem';
import CartProducts from './Assets/components/Cart';
import ProductDetail from './Assets/components/ProductDetail';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';

const App = () => {

  return (
    <Router>
      <div className="App">
        <Switch>
          <Route path='/' exact component={Home} />
          <Route path='/cart' component={CartProducts} />
          <Route path='/product-detail/:id' component={ProductDetail} />
        </Switch>
      </div>

    </Router >
  );
}

function Home() {

  const [cart, setCart] = useState([]);

  const addTocart = (product) => {
    const ProductExist = cart.find(item => item.id === product.id);
    if(ProductExist){
      setCart(cart.map(item => item.id === product.id ? {...ProductExist, quantity: ProductExist.quantity + 1} : item));
    }else{
      setCart([...cart, {...ProductExist.quantity + 1}]);
    }
  }

  return (
    <>
      <Header />
      <main>
        <div className="products">
          <ProductItem cart={cart} setCart={setCart} />
        </div>
      </main>
      <Footer />
    </>
  );
}

export default App;
