import React, { useEffect, useState } from "react";
import ReactNotification, { store } from "react-notifications-component";
import "react-notifications-component/dist/theme.css";
import "animate.css";
import DetailHeader from "./DetailHeader";
import "../../../node_modules/font-awesome/css/font-awesome.min.css";
import Footer from "./Footer";
import CartProducts from "./CartProducts";
import { useLocation } from "react-router-dom";

const Cart = () => {
  useEffect(() => {
    getSubTotalValue();
  }, []);

  const location = useLocation();
  const { cart } = location.state;
  const [newCart, setNewCart] = useState(cart);
  const [subTotal, setSubTotal] = useState(0);
  const [total, setTotal] = useState(subTotal);
  // const [getQuantity, setGetQuantity] = useState(1);

  const [checked, setChecked] = useState(false);

  const removeFromCart = (product) => {
    const newItems = newCart.filter((item) => item.id !== product);
    setNewCart(newItems);
    removeNotification();
  };

  const getSubTotalValue = () => {
    const getTotalVal = cart.reduce((a, c) => a + c.price, 0);
    setSubTotal(getTotalVal);
  };

  const totalValue = () => {
    if (checked) {
      setTotal(subTotal + 150);
    }
    console.log(total);
  };

  const removeNotification = () => {
    store.addNotification({
      title: "Warning",
      message: "Product removed from cart!",
      type: "danger",
      insert: "bottom",
      container: "top-right",
      animationIn: ["animate__animated", "animate__fadeIn"],
      animationOut: ["animate__animated", "animate__fadeOut"],
      dismiss: {
        duration: 2000,
        onScreen: false,
      },
    });
  };

  return (
    <div>
      <ReactNotification />
      <DetailHeader />
      <section className="cart-details">
        <div className="selected-cart-items">
          <ul className="selected-item-detail">
            <li>Product</li>
            <li>Price</li>
            <li>Quantity</li>
            <li>Total</li>
          </ul>
          <section className="cart-products">
            {newCart.length === 0 && <center>Cart is empty</center>}
            {newCart.map((cartItems) => (
              //   <CartProducts
              //     id={cartItems.id}
              //     cart={cartItems}
              //     key={cartItems.id}
              //     name={cartItems.title}
              //     price={cartItems.price}
              //     image={cartItems.image}
              //     removeFromCart={removeFromCart}
              //   />
              <div className="cart-prducts-wrapper">
                <ul>
                  <li>
                    <button onClick={() => removeFromCart(cartItems.id)}>
                      <i className="fa fa-remove"></i>
                    </button>
                  </li>
                  <li>
                    <div className="cart-products-img">
                      <img src={cartItems.image} />
                    </div>
                  </li>
                  <li className="cart-products-name">{cartItems.title}</li>
                  <li className="cart-products-price">${cartItems.price}</li>
                  <li className="cart-products-quantity">
                    <i
                      onClick={() => incDecQuantity("minus")}
                      className="fa fa-minus"
                    ></i>
                    <input onChange={() => {}} value={quantity} />
                    <i
                      onClick={() => incDecQuantity("plus")}
                      className="fa fa-plus"
                    ></i>
                  </li>
                  <li className="cart-products-price">${cartItems.fixedPrice}</li>
                </ul>
              </div>
            ))}
          </section>
        </div>
        <div className="cart-totals">
          <p className="cart-total-title">CART TOTALS</p>
          <ul>
            <li>
              <p className="cart-total-subtitle">Subtotal</p>
              <p className="cart-total-secondary-subtitle">
                ${subTotal.toFixed(2)}
              </p>
            </li>
            <li>
              <p className="cart-total-subtitle">Shipping</p>
              <div>
                <input
                  id="free"
                  type="checkbox"
                  onClick={() => {
                    setChecked(false);
                  }}
                  checked={!checked && "checked"}
                />
                <label className="cart-total-secondary-subtitle" htmlFor="free">
                  Standart(Free)
                </label>
                <br />
                <input
                  id="express"
                  onClick={() => totalValue()}
                  type="checkbox"
                  onClick={() => {
                    setChecked(true);
                  }}
                  checked={checked && "checked"}
                />
                <label
                  className="cart-total-secondary-subtitle"
                  htmlFor="express"
                >
                  Express
                </label>
              </div>
            </li>
            <li>
              <p className="cart-total-subtitle">Total</p>
              <p className="cart-total-payment">${total}</p>
            </li>
          </ul>
          <button onClick={() => console.log(subTotal)}>
            PROCEED TO CHECKOUT
          </button>
        </div>
      </section>
      <Footer />
    </div>
  );
};

export default Cart;
