import '../css/App.css';
import React, { Component } from 'react';

class ProductItem extends Component {
    constructor(props) {
        super(props);
    }

    render() {
        return (
            <div className="product-item">
                <img src="" className="product-img" alt={this.props.name} />
                <h3>{this.props.name}</h3>
                <p>{this.props.price}$</p>
                <div className={productItemFooter}>
                    <button className="see-product-details">
                        Details
                    </button>
                    <button className="add-to-cart">
                        Add To Cart
                    </button>
                </div>
            </div>
        );
    }
}

const productItemFooter = {
    width: 100 + "%",
    height: 40 + "px",
    display: "flex",
    position: "absolute",
    bottom: -80 + "px",
    left: 0,
    transition: .3+ "s"
}


export default ProductItem;
