import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../css/App.css';

const ProductItem = () => {

    useEffect(() => {
        fetchItems()
    }, []);

    const [items, setItems] = useState([]);
    const [cart, setCart] = useState([]);

    let isModalOpen = false;

    const fetchItems = async () => {
        const data = await fetch('http://localhost:8000/products');
        const datas = await data.json();
        setItems(datas);
    }

    const addToCartHandler = (item) => {
        setCart([...cart, items]);
    }

    const handleModal = () => {
        isModalOpen = !isModalOpen;
    }

    return (
        <>
            {
                items.map(item => (
                    <div key={item.id} className="product-item">
                        <div className="product-image">
                            <img src={item.src} alt={item.name} />
                        </div>
                        <h3>{item.name}</h3>
                        <p>{item.price}$</p>
                        <div className="product-item-footer">
                            <button onClick={() => handleModal()} className="see-product-details">
                                {/* <Link to={`/product-detail/${item.id}`}> */}
                                Details
                                {/* </Link> */}
                            </button>
                            <button onClick={() => addToCartHandler(item)} className="add-to-cart">
                                Add To Cart
                            </button>
                        </div>
                        {isModalOpen ? <Modal /> : ''}
                    </div>
                ))
            }
        </>
    );

    const Modal = () => {
        console.log(true)
        return (
            <div className="modal">
                <div className="modal-image">
                    <img src="" />
                </div>
                <div className="modal-detail">
                    <h2 className="modal-title">Product Name</h2>
                    <p className="modal-descr">Description</p>
                    <p>Category</p>
                    <p>stock</p>
                    <p className="modal-price">Price</p>
                </div>
            </div>
        );
    }
}



export default ProductItem;
