import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Header from './Header';
import Footer from './Footer';
import Modal from './Modal';
import ReactLoading from 'react-loading';
import '../css/App.css';


function Home() {

    useEffect(() => {
        fetchItems()
    }, []);

    const [items, setItems] = useState([]);
    let [cart, setCart] = useState([]);
    const [quantity, setQuantity] = useState(1);
    const [popUp, setPopUp] = useState();
    const [modalItem, setModaltem] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    let amount = 0;
    let isModalOpen = false;

    const fetchItems = async () => {
        const data = await fetch('https://fakestoreapi.com/products?limit=20').then(
            function (res) {
                return res.json()
            }).then(function (data) {
                setIsLoading(false);
                setItems(data)
            }).catch(
                function (err) {
                    console.log(err, ' error')
                }
            )
    }

    const addToCartHandler = (item) => {
        setCart([...cart, item]);
    }

    const searchProduct = (name) => {
        items.filter(item => {
            return item.name === name
        })
    }


    const getModalData = (item) => {
        setModaltem(item);
    }


    const openModal = () => {
        isModalOpen = true;
        setPopUp(isModalOpen);
    }

    const closeModal = (val) => {
        isModalOpen = val;
        setPopUp(isModalOpen);
    }




    return (
        <>
            <Header cart={cart} quantity={quantity} searchProduct={searchProduct} />
            <main>
                <Modal modalItem={modalItem} closeModal={closeModal} display={popUp} />
                <div className="products">
                    {
                        isLoading ? <ReactLoading color={"white"} height={'10%'} width={'10%'} />
                            :
                            items.map(item => (
                                <div key={item.id} className="product-item">
                                    <div className="product-image">
                                        <img src={item.image} alt={item.title} />
                                    </div>
                                    <h3>{item.title}</h3>
                                    <p>{item.price}$</p>
                                    <div className="product-item-footer">
                                        <button onClick={() => {
                                            openModal(item.id)
                                            getModalData(item)
                                        }} className="see-product-details">
                                            {/* <Link to={`/product-detail/${item.id}`}> */}
                                            Quick View
                                            {/* </Link> */}
                                        </button>
                                        <button onClick={() => addToCartHandler(item)} className="add-to-cart">
                                            Add To Cart
                                        </button>
                                    </div>

                                </div>
                            ))
                    }
                </div>
            </main>
            <Footer />
        </>
    );
}


export default Home;



