import React, { useState } from 'react';
import '../../../node_modules/font-awesome/css/font-awesome.min.css';

const CartProducts = ({ name, price, image, quantity, increaseQuantity }) => {

    // Fix stock bug
    const quantityHandler = (val, operator) => {
        if (operator === 'plus') {
            const value = val + 1;
            setQuantity(value);
        } else {
            if (val > 1) {
                const value = val - 1;
                setQuantity(value)
            }
        }
    }


    return (
        <div className="cart-prducts-wrapper">
            <ul>
                <li>
                    <button>
                        <i className="fa fa-remove"></i>
                    </button>
                </li>
                <li>
                    <div className="cart-products-img">
                        <img src={image} />
                    </div>
                </li>
                <li className="cart-products-name">{name}</li>
                <li className="cart-products-price">${price}</li>
                <li className="cart-products-quantity">
                    <button onClick={()=> quantityHandler(val, 'minus')} className="btn">
                        <i className="fa fa-minus"></i>
                    </button>
                    <input onChange={() => { }} type="number" value={quantity} />
                    <button onClick={()=> quantityHandler(val, 'plus')} className="btn">
                        <i className="fa fa-plus"></i>
                    </button>
                </li>
                <li className="cart-products-price">${price * quantity}</li>
            </ul>
        </div>
    );

}
export default CartProducts;
