import { useState, useEffect } from "react";

const CartProducts = ({
  name,
  price,
  image,
  removeFromCart,
  id,
  quantity,
  getSubTotalValue,
}) => {
  useEffect(() => {
    getSubTotalValue();
  }, []);

  // const [qnty, setQuantity] = useState(quantity);
  const amount = price * quantity;

  // const incDecQuantity = (val) => {
  //   if (qnty > 1 && val === "minus") {
  //     setQuantity(qnty - 1);
  //   } else {
  //     setQuantity(qnty + 1);
  //   }
  // };

  return (
    <div className="cart-prducts-wrapper">
      <ul>
        <li>
          <button
            onClick={(e) => {
              getSubTotalValue();
              removeFromCart(id);
              console.log(e.target.getSubTotalValue)
            }}
          >
            <i className="fa fa-remove"></i>
          </button>
        </li>
        <li>
          <div className="cart-products-img">
            <img src={image} />
          </div>
        </li>
        <li className="cart-products-name">{name}</li>
        <li className="cart-products-price">${price}</li>
        <li className="cart-products-quantity">
          <span>{quantity}</span>
          {/* <i
            onClick={() => {
              incDecQuantity("minus");
            }}
            className="fa fa-minus"
          ></i>
          <i onClick={() => incDecQuantity("plus")} className="fa fa-plus"></i> */}
        </li>
        <li className="cart-products-price">${amount.toFixed(2)}</li>
      </ul>
    </div>
  );
};
export default CartProducts;
