import { useEffect, useState } from 'react';
import '../../../node_modules/font-awesome/css/font-awesome.min.css';


const Modal = ({ display, closeModal, match, modalItem }) => {

    let showModal = display ? "modal show-modal" : "modal";

    return (
        <div className={showModal}>
            <button onClick={() => closeModal(false)}>
                <i className="fa fa-close close-modal-icon"></i>
            </button>
            <div className="modal-info-container" >
                <div className="modal-image">
                    <img src={modalItem.src} />
                </div>
                <div className="modal-detail">
                    <h2 className="modal-title">{modalItem.name}</h2>
                    <p className="modal-descr">Description: <span>{modalItem.desc}</span></p>
                    <p className="modal-category">Category: <span>{modalItem.category}</span></p>
                    <p className="modal-stock">In stock: <span>{modalItem.stock}</span> </p>
                    <p className="modal-price">Price: <span>{modalItem.price}$</span></p>
                </div>
            </div>
        </div>
    );
}

export default Modal;
