import { useState, useEffect } from 'react';
import Header from './Header';
import Footer from './Footer';
import Modal from './Modal';
import ReactLoading from 'react-loading';
import '../css/App.css';


function Home() {

    useEffect(() => {
        fetchItems()
    }, []);

    const [items, setItems] = useState([]);
    let [cart, setCart] = useState([]);
    const [quantity, setQuantity] = useState(1);
    const [popUp, setPopUp] = useState();
    const [modalItem, setModaltem] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [search, setSearch] = useState('');

    let amount = 0;
    let isModalOpen = false;

    const fetchItems = async () => {
        const data = await fetch('https://fakestoreapi.com/products?sort=desc?limit=20').then(
            function (res) {
                return res.json()
            }).then(function (data) {
                setIsLoading(false);
                setItems(data)
            }).catch(
                function (err) {
                    console.log(err, ' error')
                }
            )
    }

    const addToCartHandler = (item) => {
        setCart([...cart, item]);
    }

    const searchProduct = (product) => {
        const result = product.target.value;
        setSearch(result)
    }


    const getModalData = (item) => {
        setModaltem(item);
    }


    const openModal = () => {
        isModalOpen = true;
        setPopUp(isModalOpen);
    }

    const closeModal = (val) => {
        isModalOpen = val;
        setPopUp(isModalOpen);
    }




    return (
        <>
            <Header cart={cart} quantity={quantity} searchProduct={searchProduct} />
            <main>
                <Modal modalItem={modalItem} closeModal={closeModal} display={popUp} />
                <div className="product-wrapper">
                    <select className="product-category">
                        <option disabled>Change category</option>
                        <option>Men's clothing</option>
                        <option>Women's clothing</option>
                        <option>Jewelery</option>
                        <option>Electronics</option>
                    </select>
                    <div className="products">
                        {
                            isLoading ? <ReactLoading color={"white"} height={'10%'} width={'10%'} />
                                :
                                items.filter(val => {
                                    if (search == "") {
                                        return val;
                                    } else if (val.title.toLowerCase().includes(search.toLowerCase())){
                                        return val
                                    }
                                }).map(item => (
                                    <div key={item.id} className="product-item">
                                        <div className="product-image">
                                            <img src={item.image} alt={item.title} />
                                        </div>
                                        <h3>{item.title}</h3>
                                        <p>{item.price}$</p>
                                        <div className="product-item-footer">
                                            <button onClick={() => {
                                                openModal(item.id)
                                                getModalData(item)
                                            }} className="see-product-details">
                                                Quick View
                                            </button>
                                            <button onClick={() => addToCartHandler(item)} className="add-to-cart">
                                                Add To Cart
                                            </button>
                                        </div>

                                    </div>
                                ))
                        }
                    </div>
                </div>
            </main>
            <Footer />
        </>
    );
}


export default Home;
