import React, { useState, useEffect } from 'react';
import Footer from './Assets/components/Footer';
import Header from './Assets/components/Header';
import ProductItem from './Assets/components/ProductItem';
import ProductData from './Data/JsonData.json';
import CartProducts from './Assets/components/Cart';
import ProductDetail from './Assets/components/ProductDetail';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';

const App = () => {

  return (
    <Router>
      <div className="App">
        <Route path='/' exact component={Home} />
        <Route path='/cart' component={CartProducts} />
        <Route path='/product-detail' component={ProductDetail} />
      </div>

    </Router >
  );
}

const Home = () => {

  const [items, setItems] = useState([null]);


    fetch('./Data/JsonData.json').then((res) => {
      return res.json();
    }).then((data) => {
      setItems(data);
    }).catch(e => 'error');

  console.log(items)



  return (
    <>
      <Header />
      <main>
        <div className="products">

          {/* {items.map(data => {
            return <ProductItem key={data.id} image={data.src} name={data.name} price={data.price} />
          })} */}
        </div>
      </main>
      <Footer />
    </>
  );
}

export default App;
