import React from 'react';
import DetailHeader from './DetailHeader';
import '../../../node_modules/font-awesome/css/font-awesome.min.css';
import '../css/App.css';
import Footer from './Footer';
import CartProducts from './CartProducts';
import { useLocation } from 'react-router-dom';


const Cart = () => {

    const location = useLocation();
    const { cart } = location.state;




    return (
        <div>
            {/* <DetailHeader /> */}
            <section className="cart-details">
                <div className="selected-cart-items">
                    <ul className="selected-item-detail">
                        <li>Product</li>
                        <li>Price</li>
                        <li>Quantity</li>
                        <li>Total</li>
                    </ul>
                    <section className="cart-products">
                        {cart.map(cartItems => <CartProducts 
                        key={cartItems.id} 
                        name={cartItems.name}  
                        price={cartItems.price}
                        image={cartItems.src} />)}
                    </section>
                </div>
                <div className="cart-totals">
                    <p className="cart-total-title">CART TOTALS</p>
                    <ul>
                        <li>
                            <p className="cart-total-subtitle">Subtotal</p>
                            <p className="cart-total-secondary-subtitle">$4013</p>
                        </li>
                        <li>
                            <p className="cart-total-subtitle">Shipping</p>
                            <div>
                                <input id="free" type="checkbox" />
                                <label className="cart-total-secondary-subtitle" htmlFor="free">Standart(Free)</label>
                                <br />
                                <input id="express" type="checkbox" />
                                <label className="cart-total-secondary-subtitle" htmlFor="express">Express</label>
                            </div>
                        </li>
                        <li>
                            <p className="cart-total-subtitle">Total</p>
                            <p className="cart-total-payment">$4013</p>
                        </li>

                    </ul>
                    <button>PROCEED TO CHECKOUT</button>
                </div>
            </section>
            <Footer />
        </div>
    );

}


export default Cart
