import React, { useEffect, useState } from 'react';
import '../../../node_modules/font-awesome/css/font-awesome.min.css';
import DetailHeader from './DetailHeader';
import Footer from './Footer';
import '../css/App.css';


const ProductDetail = ({ match }) => {

    useEffect(() => {
        fetchItem();
    }, []);


    const [item, setItem] = useState({});
    const [cart, setCart] = useState([
        {
            id: item.id,
            name: item.name,
            price: item.price,
            image: item.src
        }
    ]);
    const [quantity, setQuantity] = useState(1);


    const fetchItem = async () => {
        const fetchItem = await fetch(`http://localhost:8000/products/${match.params.id}`);
        const data = await fetchItem.json();
        setItem(data);
    }

    const addToCart = (item) => {
        setCart([...cart, item]);
    }


    // Fix stock bug
    const increaseQuantity = (val) => {
        const value = val + 1;
        setQuantity(value);
    }
    const dicreaseQuantity = (val) => {
        if (val > 1) {
            const value = val - 1;
            setQuantity(value)
        }   
    }


    return (
        <>
            <DetailHeader cart={cart} />
            <section className="detail-section">
                <div className="img-section">
                    <img src={item.src} />
                </div>
                <div className="desc-section">
                    <h2 className="detail-title">{item.name}</h2>
                    <p className="detail-description">{item.desc}</p>
                    <p className="detail-price">{item.price}$</p>
                    <div className="detail-quantity">
                        <div className="detail-q">

                            <button onClick={() => dicreaseQuantity(quantity)} className="btn-minus">
                                <i className="fa fa-minus"></i>
                            </button>
                            <input onChange={() => { }} type="number" value={quantity} />
                            <button onClick={() => increaseQuantity(quantity)} className="btn-plus">
                                <i className="fa fa-plus"></i>
                            </button>
                        </div>

                        <button  onClick={() => addToCart(item)} className="detail-add-to-cart-btn">ADD TO CART</button>
                    </div>
                </div>
            </section>
            <Footer />
        </>
    );

}



export default ProductDetail;
