    const [num, setNum] = useState('');
    const [expire, setExpire] = useState('');
    const [cvv, setCvv] = useState('');

    let toggle = checkOut ? "checkout show-checkout" : "checkout";

    const simpleValidation = () => {
        if(name.length > 0 && name != ''){
            console.log('test')
        }
    }

  return (
    <section className={toggle}>
      <div className="checkout-header">
        <i onClick={() => checkOutClose(false)} className="fa fa-remove"></i>
        <h3>Card Details</h3>
        <img src={CardImage} alt="Sample Card" />
      </div>
      <div className="card-info">
        <ul>
          <li>
            <input onChange={(e) => setName(e.target.value)} type="text" placeholder="Name On Card" />
          </li>
          <li>
            <input onChange={(e) => setNum(e.target.value)} type="text" placeholder="Card Number" />
          </li>
          <li>
            <label htmlFor="ExDate">Expiration Date: </label>
            <input onChange={(e) => setExpire(e.target.value)} type="date" id="ExDate" />
          </li>
          <li>
            <input onChange={(e) => setCvv(e.target.value)} maxLength="3" placeholder="CVV" type="text" />
          </li>
        </ul>
