// import '../../../node_modules/font-awesome/css/font-awesome.min.css';

const CartProducts = ({ name, price, image, quantity, removeFromCart, id }) => {

    const qnty = parseInt(quantity);
    const Price = price * qnty;
    const fixedPrice = Price.toFixed(2)


    return (
        <div className="cart-prducts-wrapper">
            <ul>
                <li>
                    <button onClick={() => removeFromCart(id)}>
                        <i className="fa fa-remove"></i>
                    </button>
                </li>
                <li>
                    <div className="cart-products-img">
                        <img src={image} />
                    </div>
                </li>
                <li className="cart-products-name">{name}</li>
                <li className="cart-products-price">${price}</li>
                <li className="cart-products-quantity">
                    <p>{qnty}</p>
                </li>
                <li className="cart-products-price">${fixedPrice}</li>
            </ul>
        </div>
    );

}
export default CartProducts;
