import React, { useState } from 'react';
import '../../../node_modules/font-awesome/css/font-awesome.min.css';

const CartProducts = ({ key, cart, name, price, image }) => {

    const [cartList, setCartList] = useState()

    const removeItem = (item) => {
        let items = [...cart];
        items = items.filter(removeCart => removeCart.id !== item.id);
        setCartList(items);
    }


    return (
        <div className="cart-prducts-wrapper">
            <ul>
                <li>
                    <button onClick={removeItem(key)}>
                        <i className="fa fa-remove"></i>
                    </button>
                </li>
                <li>
                    <div className="cart-products-img">
                        <img src={image} />
                    </div>
                </li>
                <li className="cart-products-name">{name}</li>
                <li className="cart-products-price">${price}</li>
                <li className="cart-products-quantity">
                    <button className="btn">
                        <i className="fa fa-minus"></i>
                    </button>
                    <input onChange={() => { }} type="number" value="1" />
                    <button className="btn">
                        <i className="fa fa-plus"></i>
                    </button>
                </li>
                <li className="cart-products-price">$594</li>
            </ul>
        </div>
    );

}
export default CartProducts;
