import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Header from './Header';
import Footer from './Footer';
import Modal from './Modal';
import '../css/App.css';


function Home() {

    useEffect(() => {
        fetchItems()
    }, []);

    const [items, setItems] = useState([]);
    const [cart, setCart] = useState([]);
    const [popUp, setPopUp] = useState(null);

    let isModalOpen = false;

    const fetchItems = async () => {
        const data = await fetch('http://localhost:8000/products');
        const datas = await data.json();
        setItems(datas);
    }

    const addToCartHandler = (item) => {
        setCart([...cart, items]);
    }

    const openModal = () => {
        isModalOpen = true;
        setPopUp(isModalOpen);
    }

    const closeModal = (val) => {
        isModalOpen = val;
        setPopUp(isModalOpen);
    }
    


    return (
      <>
        <Header name={cart.name} />
        <main>
            <Modal closeModal={closeModal} display={popUp}  />
          <div className="products">
          {
                items.map(item => (
                    <div key={item.id} className="product-item">
                        <div className="product-image">
                            <img src={item.src} alt={item.name} />
                        </div>
                        <h3>{item.name}</h3>
                        <p>{item.price}$</p>
                        <div className="product-item-footer">
                            <button onClick={() => openModal()} className="see-product-details">
                                {/* <Link to={`/product-detail/${item.id}`}> */}
                                Preview
                                {/* </Link> */}
                            </button>
                            <button onClick={() => addToCartHandler(item)} className="add-to-cart">
                                Add To Cart
                            </button>
                        </div>
                        
                    </div>
                ))
            }
          </div>
        </main>
        <Footer />
      </>
    );
  }

  
  export default Home;

  

