import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Header from './Header';
import Footer from './Footer';
import Modal from './Modal';
import '../css/App.css';


function Home() {

    useEffect(() => {
        fetchItems()
    }, []);

    const [items, setItems] = useState([]);
    const [cart, setCart] = useState([]);
    const [quantity] = useState(1);
    const [popUp, setPopUp] = useState();

    let isModalOpen = false;

    const fetchItems = () => {
        const data = fetch("./JsonData.json").then(
            function (res) {
                return res.json()
            }).then(function (data) {
                setItems(data)
            }).catch(
                function (err) {
                    console.log(err, ' error')
                }
            )
    }

    const addToCartHandler = (items) => {
        const ProductExist = cart.filter(item => item.id === items.id);
        if (ProductExist) {
            setCart(cart.map(item => item.id === item.id
                ? { ...ProductExist, quantity: ProductExist.quantity + 1 }
                : items))
        } else {
            setCart([...cart, { ...items, quantity: 1 }]);
        }

    }

    const openModal = () => {
        isModalOpen = true;
        setPopUp(isModalOpen);
    }

    const closeModal = (val) => {
        isModalOpen = val;
        setPopUp(isModalOpen);
    }




    return (
        <>
            <Header cart={cart} quantity={quantity} />
            <main>
                <Modal closeModal={closeModal} display={popUp} />
                <div className="products">
                    {
                        items.map(item => (
                            <div key={item.id} className="product-item">
                                <div className="product-image">
                                    <img src={item.src} alt={item.name} />
                                </div>
                                <h3>{item.name}</h3>
                                <p>{item.price}$</p>
                                <div className="product-item-footer">
                                    <button onClick={() => openModal()} className="see-product-details">
                                        {/* <Link to={`/product-detail/${item.id}`}> */}
                                        Quick View
                                        {/* </Link> */}
                                    </button>
                                    <button onClick={() => addToCartHandler(item)} className="add-to-cart">
                                        Add To Cart
                                    </button>
                                </div>

                            </div>
                        ))
                    }
                </div>
            </main>
            <Footer />
        </>
    );
}


export default Home;



