import React, { Component } from 'react';
import ProductData from '../../Data/JsonData.json';
import '../../../node_modules/font-awesome/css/font-awesome.min.css';
import DetailHeader from './DetailHeader';
import Footer from './Footer';
import '../css/App.css';


const ProductDetail = ({ match }) => {

    const fetchDetails = async () => {
        const data = await fetch(ProductData);
        console.log(data);
    }

    return (
        <>
            <DetailHeader />
            <section className="detail-section">
                <div className="img-section">
                    <img src="../../Data/product_images/AlmondToeCourtShoes.jpeg" />
                </div>
                <div className="desc-section">
                    <h2 className="detail-title">Men's Shoes</h2>
                    <p className="detail-description">Lorem ipsum dolo sit amet</p>
                    <p className="detail-price">599$</p>
                    <div className="detail-quantity">
                        <div className="detail-q">
                            <button className="btn-minus">
                                <i className="fa fa-minus"></i>
                            </button>
                            <input onChange={() => { }} type="number" value="1" />
                            <button className="btn-plus">
                                <i className="fa fa-plus"></i>
                            </button>
                        </div>
                        <button className="detail-add-to-cart-btn">ADD TO CART</button>
                    </div>
                </div>
            </section>
            <Footer />
        </>
    );

}



export default ProductDetail;
