import React, { useEffect, useState } from "react";
import "../../../node_modules/font-awesome/css/font-awesome.min.css";
import DetailHeader from "./DetailHeader";
import Footer from "./Footer";
import "../css/App.css";

const ProductDetail = ({ match }) => {
  useEffect(() => {
    fetchItem();
  }, []);

  const [item, setItem] = useState({});
  const [cart, setCart] = useState([]);
  const [quantity, setQuantity] = useState(1);
  const [buttonText, setButtonText] = useState("ADD TO CART");
  const [disableBtn, setDisableBtn] = useState(false);

  const fetchItem = async () => {
    const fetchItem = await fetch(
      `http://localhost:8000/products/${match.params.id}`
    );
    const data = await fetchItem.json();
    setItem(data);
  };

  const addToCart = (item, btnText) => {
    setCart([...cart, item]);
    if (disableBtn === false) {
      setButtonText(btnText);
      setDisableBtn(true);
    }
  };

  const removeProduct = (product) => {
    // const newList = setCart(cart.filter(item => item.id !== product.id));
    // setCart(newList);
    console.log(product.id);
  };

  // Fix stock bug
  const quantityHandler = (val, operator) => {
    if (!disableBtn) {
      if (operator === "plus") {
        const value = val + 1;
        setQuantity(value);
      } else {
        if (val > 1) {
          const value = val - 1;
          setQuantity(value);
        }
      }
    }
  };

  return (
    <>
      <DetailHeader cart={cart} quantity={quantity} />
      <section className="detail-section">
        <div className="img-section">
          <img src={item.src} />
        </div>
        <div className="desc-section">
          <h2 className="detail-title">{item.name}</h2>
          <p className="detail-description">{item.desc}</p>
          <p className="detail-price">{item.price}$</p>
          <div className="detail-quantity">
            <div className="detail-q">
              <button
                onClick={() => quantityHandler(quantity, "minus")}
                className="btn-minus"
              >
                <i className="fa fa-minus"></i>
              </button>
              <input onChange={() => {}} type="number" value={quantity} />
              <button
                onClick={() => quantityHandler(quantity, "plus")}
                className="btn-plus"
              >
                <i className="fa fa-plus"></i>
              </button>
            </div>
            <button
              onClick={() => addToCart(item, "Product Added to Cart")}
              disabled={disableBtn}
              className="detail-add-to-cart-btn"
            >
              {buttonText}
            </button>
          </div>
        </div>
      </section>
      <Footer />
    </>
  );
};

export default ProductDetail;
