import { useState, useEffect } from 'react';
import Header from './Header';
import Footer from './Footer';
import Modal from './Modal';
import ReactLoading from 'react-loading';
import '../css/App.css';


function Home() {

    useEffect(() => {
        fetchItems()
    }, []);

    const [items, setItems] = useState([]);
    let [cart, setCart] = useState([]);
    const [quantity, setQuantity] = useState(1);
    const [popUp, setPopUp] = useState();
    const [modalItem, setModaltem] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [search, setSearch] = useState('');
    const [ctgry, setCtgry] = useState('');

    
    let isModalOpen = false;

    const fetchItems = async () => {
        const data = await fetch('https://fakestoreapi.com/products').then(
            function (res) {
                return res.json()
            }).then(function (data) {
                setIsLoading(false);
                setItems(data)
            }).catch(
                function (err) {
                    console.log(err, ' error')
                }
            )
    }

    const addToCartHandler = (item) => {
        setCart([...cart, item]);
    }

    const searchProduct = (product) => {
        const result = product.target.value;
        setSearch(result)
    }


    const getModalData = (item) => {
        setModaltem(item);
    }


    const openModal = () => {
        isModalOpen = true;
        setPopUp(isModalOpen);
    }

    const closeModal = (val) => {
        isModalOpen = val;
        setPopUp(isModalOpen);
    }

    const categoryHandler = () => {
        // const updateItems = items.filter(item => {
        //     return item.category === categoryItem
        // });
        // setItems(updateItems);
        fetchItems()
    }





    return (
        <>
            <Header cart={cart} quantity={quantity} searchProduct={searchProduct} />
            <main>
                <Modal modalItem={modalItem} closeModal={closeModal} display={popUp} />
                <div className="product-wrapper">
                    <select className="product-category">
                        <option disabled>Change category</option>
                        <option onClick={() => categoryHandler()}>all</option>
                        <option onClick={() => setCtgry("men's clothing")}>Mens Casualwear</option>
                        <option onClick={() => setCtgry("women's clothing")}>Womens Casualwear</option>
                        <option onClick={() => setCtgry("jewelery")}>Jewelery</option>
                        <option onClick={() => setCtgry("electronics")}>Electronics</option>
                    </select>
                    <div className="products">
                        {
                            isLoading ? <ReactLoading color={"white"} height={'10%'} width={'10%'} />
                                :
                                items.filter(val => {
                                    // if (search == "") {
                                    //     return val;
                                    // } else if (val.title.toLowerCase().includes(search.toLowerCase())) {
                                    //     return val
                                    // }
                                   return val.category === ctgry
                                }).map(item => (
                                    <div key={item.id} className="product-item">
                                        <div className="product-image">
                                            <img src={item.image} alt={item.title} />
                                        </div>
                                        <h3>{item.title}</h3>
                                        <p>{item.price}$</p>
                                        <div className="product-item-footer">
                                            <button onClick={() => {
                                                openModal(item.id)
                                                getModalData(item)
                                            }} className="see-product-details">
                                                Quick View
                                            </button>
                                            <button onClick={() => addToCartHandler(item)} className="add-to-cart">
                                                Add To Cart
                                            </button>
                                        </div>

                                    </div>
                                ))
                        }
                    </div>
                </div>
            </main>
            <Footer />
        </>
    );
}


export default Home;
