import { useEffect, useState } from 'react';
import '../../../node_modules/font-awesome/css/font-awesome.min.css';


const Modal = ({ display, closeModal, items }) => {

    useEffect(()=>{
        fetchItem();
    }, [])
    console.log(items)

    const [item, setItem] = useState({});

    const fetchItem = async () => {
        const data = await fetch(`http://localhost:8000/products/${match.params.id}`);
        const getData = data.json();
        setItem(getData);
    }

    let showModal = display ? "modal show-modal" : "modal";

    return (
        <div className={showModal}>
            <button onClick={() => closeModal(false)}>
                <i className="fa fa-close close-modal-icon"></i>
            </button>
            <div  >
                <div className="modal-image">
                    <img src="" />
                </div>
                <div className="modal-detail">
                    <h2 className="modal-title">{item.name}</h2>
                    <p className="modal-descr">Description</p>
                    <p>Category</p>
                    <p>stock</p>
                    <p className="modal-price">Price</p>
                </div>
            </div>
        </div>
    );
}

export default Modal;
